INSERT INTO content_blocks (key, title, body_md) VALUES
('rules', 'Lake Rules', '# Lake Rules\n\n1. All fishing must be done with a valid permit\n2. No littering allowed\n3. Fish must be released if under 10 inches\n4. No motorboats allowed\n5. All equipment must be cleaned before leaving'),
('bait', 'Bait Information', '# Bait Information\n\nWe provide a wide range of high-quality bait for carp fishing. All our bait is sourced from trusted suppliers and is suitable for the lake environment.\n\n## Pre-order Requirements\n\nSome bait items require pre-ordering. Please check the pre-order deadline for each product.'),
('bait-boat-hire', 'Bait Boat Hire', '# Bait Boat Hire\n\nWe offer bait boat hire for those who want to explore the lake more effectively.\n\n## Pricing\n\n- Standard Bait Boat: £150 per week\n- Premium Bait Boat: £50 per day\n\n## Terms\n\nAll bait boats must be returned by 6pm on your final day.'),
('stock', 'Lake Stock', '# Lake Stock\n\nOur lake is home to a variety of carp species including:\n\n- Common Carp (10-20 lbs)\n- Mirror Carp (8-15 lbs)\n- Grass Carp (15-30 lbs)\n\n## Fishing Season\n\nThe fishing season runs from April to October.'),
('contact', 'Contact Information', '# Contact Us\n\nEmail: info@carpfishinglake.com\nPhone: +33 123 456 789\n\nAddress: 123 Lake Road, France\n\nWe are open daily from 8am to 8pm.');
